package com.anýl.conditions;

public class WhileExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 4;
		
		
		
		while (a < 10) {
			System.out.println(a + " value a smaller than 10");
			a ++;
			
		}
		
		System.out.println(".................");
		
		do {
			
			System.out.println("ýn the do while");
			
		}while(a < 10);
		

	}

}
